mapper.update:
update article
set like_count = #{likesCount}
where id = #{article_id}

update article
set like_count = like_count + #{likesCount}
where id = #{user_id}

mapper.select:
SELECT u.username
FROM user u
         JOIN follow f ON u.id = f.follower_id
WHERE f.following_id = (
    SELECT id FROM user WHERE username = #{username}
)
